from core_lib.data_models.model import Model, EntityType


USER_ENTITY_TYPE = EntityType.User
USER_ASSOCIATION_ENTITY_TYPE = EntityType.UserAssociation


class User(Model):
    def __init__(self, sk: str, name: str):
        entity_type = USER_ENTITY_TYPE
        super().__init__(pk=entity_type.value, sk=sk, entity_type=entity_type)
        self.name = name
