from core_lib.data_models.user.impl.dynamodb import user_dynamodb_data_access
from core_lib.data_models.user.impl.dynamodb.user_dynamodb_data_model import (
    USER_ENTITY_TYPE,
)

USER_DATA_ACCESS_IMPL = user_dynamodb_data_access


def find_user_by_id(_id: str):
    return USER_DATA_ACCESS_IMPL.find_user_by_id(_id=_id)


def delete_user_by_id(_id: str):
    return USER_DATA_ACCESS_IMPL.delete_user_by_id(_id=_id)


def create_user(user):
    return USER_DATA_ACCESS_IMPL.create_user(user=user)


def update_user(_id: str, updates: dict):
    return USER_DATA_ACCESS_IMPL.update_user(_id=_id, updates=updates)


def query_all_users():
    return USER_DATA_ACCESS_IMPL.query_all_users()


def query_associated_users(user_a_id: str, user_b_id: str = None):
    return USER_DATA_ACCESS_IMPL.query_associated_users(user_a_id, user_b_id)


def associate_users(user_a_id: str, user_b_id: str):
    return USER_DATA_ACCESS_IMPL.associate_users(user_a_id, user_b_id)


def query_associated_users_with_profiles(user_a_id: str, user_b_id: str = None):
    return USER_DATA_ACCESS_IMPL.query_associated_users_with_profiles(
        user_a_id=user_a_id, user_b_id=user_b_id
    )
