class AppException(Exception):
    """AppException as base class for application wide exception is used to handle exception message"""

    def __init__(self, message=None, code=None, params=None):
        self.message = message
        self.code = code
        self.params = params
        super(AppException, self).__init__(message, code, params)

    def get_code(self):
        """return code"""
        return self.code

    def get_message(self):
        """return message"""
        return self.message

    def __str__(self):
        """String representation of Exception
        :return:
        """
        return self.message
