from core_lib.services.parameter_service import get_parameter_value, ParameterName
import boto3

cognito_idp_client = boto3.client('cognito-idp')

USER_POOL_ID = None


def get_user_pool_id():
    global USER_POOL_ID

    if USER_POOL_ID is None:
        USER_POOL_ID = get_parameter_value(ParameterName.USER_POOL_ID)

    return USER_POOL_ID
