from core_lib.services.database.impl import dynamodb_service
from core_lib.utils.log_util import log_function_call

DB_SERVICE_IMPL = dynamodb_service


@log_function_call
def put_db_item(**kwargs):
    return DB_SERVICE_IMPL.put_item(**kwargs)


@log_function_call
def query_db_items(**kwargs):
    return DB_SERVICE_IMPL.query_items(**kwargs)


@log_function_call
def get_db_item(**kwargs):
    return DB_SERVICE_IMPL.get_item(**kwargs)


@log_function_call
def update_db_item(**kwargs):
    return DB_SERVICE_IMPL.update_item(**kwargs)


@log_function_call
def delete_db_item(**kwargs):
    return DB_SERVICE_IMPL.delete_item(**kwargs)
