from core_lib.services.parameter.impl import ssm_service
from enum import Enum

PARAMETER_SERVICE_IMPL = ssm_service


class ParameterName(Enum):
    USER_POOL_ID = "USER_POOL_ID"
    USER_POOL_CLIENT = "USER_POOL_CLIENT"
    DB_TABLE = "DB_TABLE"
    ACCESS_LOG_QUEUE_URL = "ACCESS_LOG_QUEUE_URL"
    ERROR_LOG_QUEUE_URL = "ERROR_LOG_QUEUE_URL"


def get_parameter_value(parameter_name: ParameterName, with_decryption: bool = None):
    return PARAMETER_SERVICE_IMPL.get_parameter_value(
        parameter_name=parameter_name, with_decryption=with_decryption
    )
