import os

from core_lib.services.queue.impl import sqs_service

QUEUE_SERVICE_IMPL = sqs_service


def get_auth_event_queue_url(region: str = os.getenv("region")):
    return QUEUE_SERVICE_IMPL.get_auth_event_queue_url(region=region)


def send_message_to_queue(
    message, queue_url: str, region: str = os.getenv("region"), delay_seconds: int = 0
):
    if queue_url is None:
        return

    return QUEUE_SERVICE_IMPL.send_message_to_queue(
        message=message, queue_url=queue_url, region=region, delay_seconds=delay_seconds
    )
