from core_lib.services.queue.impl import sqs_service

QUEUE_SERVICE_IMPL = sqs_service


def get_access_log_queue_url():
    return QUEUE_SERVICE_IMPL.get_access_log_queue_url()


def get_error_log_queue_url():
    return QUEUE_SERVICE_IMPL.get_error_log_queue_url()


def send_message_to_queue(message, queue_url: str, delay_seconds: int = 0):
    return QUEUE_SERVICE_IMPL.send_message_to_queue(
        message=message, queue_url=queue_url, delay_seconds=delay_seconds
    )
