from unittest.mock import MagicMock, patch

import core_lib.services.logs.log_service as ls

# You can replace these with your actual values
log_group = "your_log_group"
log_stream = "your_log_stream"
log_events = ["event1", "event2"]


@patch("core_lib.services.logs.log_service.LOG_SERVICE_IMPL")
def test_log_subscribed_log_events(mock_service_impl):
    # Mock the log_subscribed_log_events function
    mock_service_impl.log_subscribed_log_events = MagicMock()

    # Call the log_subscribed_log_events function
    ls.log_subscribed_log_events(
        logGroupName=log_group, logStreamName=log_stream, logEvents=log_events
    )

    # Assert that the log_subscribed_log_events function was called
    mock_service_impl.log_subscribed_log_events.assert_called_once_with(
        logGroupName=log_group, logStreamName=log_stream, logEvents=log_events
    )
