from unittest.mock import patch

from core_lib.services.secrets import secrets_service


@patch("core_lib.services.secrets.secrets_service.SECRETS_SERVICE_IMPL")
def test_get_secret_value(mock_secrets_service):
    secret_id = "test_secret_id"
    secret_value = "Secret value"
    mock_secrets_service.get_secret_value.return_value = {"SecretString": secret_value}

    result = secrets_service.get_secret_value(secret_id)

    assert result == {"SecretString": "Secret value"}
