from core_lib.utils.args_util import get_non_null_kwargs


def test_no_kwargs():
    result = get_non_null_kwargs()
    assert result == {}


def test_single_null_kwarg():
    result = get_non_null_kwargs(arg1=None)
    assert result == {}


def test_single_non_null_kwarg():
    result = get_non_null_kwargs(arg1='value')
    assert result == {'arg1': 'value'}


def test_multiple_kwargs_with_null_values():
    result = get_non_null_kwargs(arg1=None, arg2=None, arg3=None)
    assert result == {}


def test_multiple_kwargs_with_non_null_values():
    result = get_non_null_kwargs(arg1='value1', arg2='value2', arg3='value3')
    assert result == {'arg1': 'value1', 'arg2': 'value2', 'arg3': 'value3'}


def test_mixed_kwargs_with_null_and_non_null_values():
    result = get_non_null_kwargs(arg1='value1', arg2=None, arg3='value3', arg4=None)
    assert result == {'arg1': 'value1', 'arg3': 'value3'}


def test_kwargs_with_different_types_of_values():
    result = get_non_null_kwargs(arg1=123, arg2='string', arg3=None, arg4=True, arg5=[])
    assert result == {'arg1': 123, 'arg2': 'string', 'arg4': True, 'arg5': []}
