from threading import current_thread

from core_lib.utils import thread_util


def test_safe_get_thread_attribute_exists():
    current_thread().__setattr__("example_attr", "test_value")
    result = thread_util.safe_get_thread_attribute("example_attr")
    assert result == "test_value"


def test_safe_get_thread_attribute_not_exists():
    result = thread_util.safe_get_thread_attribute("non_existing_attr")
    assert result is None
